import React, {useState} from 'react'
import M from 'materialize-css'
import {useHistory} from 'react-router-dom'

const CreatePost = () =>{
    const history = useHistory()
    const [title, setTitle] = useState("")
    const [body, setBody] = useState("")
    const [image, setImage] = useState("")
    const [url, setUrl] = useState("")

    const postDetails = () =>{
        const data = new FormData()
        data.append("file", image)
        data.append("upload_preset", "usmforum")
        data.append("clooud_name", "dfoc7c90v")
        
        fetch("https://api.cloudinary.com/v1_1/dfoc7c90v/image/upload", {
            method:"post",
            body: data
        })
        .then(res=>res.json)
        .then(data=>{
            setUrl(data.url)
        })
        .catch(err=>{
            console.log(err)
        })

        fetch("/createpost",{
            method: "post",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                title,
                body,
                pic:url
            })
        }).then(res=>res.json())
        .then(data=>{
            if(data.error){
                M.toast({html: data.error})
            }
            else{   //Success
                M.toast({html:"Created post successfully!"})
                history.push('/')
            }
        })
        .catch(err=>{
            console.log(err)
        })
    }

    return(
        <div className="card input-filed"
        style={{
            margin:"30px auto",
            maxWidth:"500px",
            padding:"20px",
            textAlign:"center"
        }}
        >
            <input type="text" 
                placeholder="Title"
                value={title}
                onChange={
                    (e)=>setTitle(e.target.value)
            }/>
            <input type="text" 
                placeholder="Body"
                value={body}
                onChange={
                    (e)=>setTitle(e.target.value)
            }/>


            <div className="file-field input-field">
                <div className="btn waves-effect waves-light #64b5f6 blue lighten-2">
                    <span>Attachment</span>
                    <input type="file"
                    onChange={(e)=> setImage(e.target.files[0])}
                    />
                </div>
                <div className="file-path-wrapper">
                    <input className="file-path validate" type="text"/>
                </div>
            </div>

            <button className="btn waves-effect waves-light #64b5f6 blue lighten-2"
            onClick={()=>postDetails()}
            >
                Submit
            </button>
        </div>
    )
}

export default CreatePost
